    { heroid = 88, name = "少年龙吉", rank = 4, description = "乃昊天上帝之女，瑶池金母所生，助武王伐纣，精通水系法术，是周军得力战将之一，曾打败罗宣，砍伤孔宣，后被姜子牙封神，成为红鸾星。", autograph = "鱼尾金冠霞彩飞，身穿白鹤绛绡衣。", unlock = 1, star = 1, type = 3, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 325, attackprocess = "#013220213", hp = 1500, powermax = 1200, physicalattack = 100, physicaldefence = 100, manaattack = 150, manadefence = 100, hit = 100, dodge = 0, crit = 20, crittimes = 200, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 39, },
    { heroid = 89, name = "少年后羿", rank = 4, description = "后羿，本称羿、大羿、司羿，，帝尧的射师，嫦娥的丈夫，射日英雄。羿在年仅五岁的时候被父母抛弃在深山，自幼在山林中成长。他善于射箭，助帝尧射下九日，匡扶黎明。", autograph = "共工触天补女娲，后羿射之摧九乌。", unlock = 1, star = 1, type = 3, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 340, attackprocess = "#01020120", hp = 1500, powermax = 800, physicalattack = 150, physicaldefence = 100, manaattack = 100, manadefence = 100, hit = 150, dodge = 0, crit = 20, crittimes = 200, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 20, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 32, },
    { heroid = 90, name = "少年女娲", rank = 4, description = "女娲是古代传说中中华民族人文始祖，是神话中的创世女神，女娲人首蛇身。以泥土造人，后因时间天塌地陷，于是熔彩石以补天，斩龟足以撑天。留下女娲补天的神话传说", autograph = "若世界没有人，那该有多寂寞…", unlock = 1, star = 1, type = 2, gender = 2, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 325, attackprocess = "#2010010200100102001", hp = 1500, powermax = 1500, physicalattack = 100, physicaldefence = 100, manaattack = 150, manadefence = 100, hit = 100, dodge = 0, crit = 20, crittimes = 200, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 20, },
    { heroid = 91, name = "少年孔宣", rank = 4, description = "原为“瞠目细冠红孔雀”（世间第一只孔雀），殷商驻三山关的镇关总兵，后调往镇守金鸡岭，骑马持刀，独门神通是“五色神光”，五行内无物不收。", autograph = "天下虽大，其一人可往。", unlock = 1, star = 1, type = 2, gender = 1, position = 3, weapon = 1, species = 1, scale = 0.48, attackrange = 289, attackprocess = "#210120", hp = 1500, powermax = 1200, physicalattack = 100, physicaldefence = 100, manaattack = 150, manadefence = 100, hit = 100, dodge = 0, crit = 20, crittimes = 200, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 20, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 4, },
    { heroid = 92, name = "少年云霄", rank = 2, description = "截教通天教主座下内门亲传弟子，三霄里最年长的仙女，为人善良，法力高强，云霄仙子在开天辟地前就已经成就道行，在三仙岛上化形。已经斩尽三尸，抛尽六气。", autograph = "云髻双蟠道德清，红袍白鹤顶珠缨。", unlock = 1, star = 1, type = 2, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 308, attackprocess = "#03210023000102", hp = 1000, powermax = 1200, physicalattack = 55, physicaldefence = 30, manaattack = 58, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 40, },
    { heroid = 93, name = "少年胡喜媚", rank = 3, description = "千年九头雉鸡精，轩辕三妖之一排行第二，姐姐为苏妲己，妹妹为柳琵琶，奉女娲之命断送商朝。因轩辕坟被烧，才以妲己的义妹身份入宫，并化名胡喜媚，成为商纣王的宠妃。", autograph = "我乃轩辕三妖之二，千年九头雉鸡精。", unlock = 1, star = 1, type = 3, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 275, attackprocess = "#021020010210", hp = 1000, powermax = 800, physicalattack = 58, physicaldefence = 30, manaattack = 55, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 37, },
    { heroid = 94, name = "少年瑶姬", rank = 2, description = "炎帝之女，名曰瑶姬，未嫁而死，葬于巫山之阳，巫山神女斩杀十二妖龙、精魂依草，实为灵芝。为行船指点航路，为百姓驱除虎豹，为人间耕云播雨，为治病育种灵芝。", autograph = "昔游三峡见巫山，见画巫山宛相似。", unlock = 1, star = 1, type = 3, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 200, attackprocess = "#0120200201111111", hp = 1000, powermax = 1200, physicalattack = 58, physicaldefence = 30, manaattack = 55, manadefence = 30, hit = 100, dodge = 20, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 29, },
    { heroid = 95, name = "少年柳琵琶", rank = 4, description = "鬼方国的郡主，轩辕三妖之一排行第三，其实她是玉石琵琶精，她嫁给李靖，她之后害死殷十娘和云中子，最后，她死在杨戬的天眼之下。", autograph = "我乃轩辕三妖之三，玉石琵琶精。", unlock = 1, star = 1, type = 2, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 260, attackprocess = "#021001", hp = 1500, powermax = 1400, physicalattack = 100, physicaldefence = 100, manaattack = 150, manadefence = 100, hit = 100, dodge = 0, crit = 20, crittimes = 200, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 100, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 27, },
    { heroid = 96, name = "少年子牙", rank = 4, description = "相传姜子牙７２岁时在渭水之滨的磻溪垂钓，遇到了求贤若渴的周文王，被周武王尊为“师尚父”。后辅佐武王伐纣建立了周朝。", autograph = "姜太公在此，诸神退散！", unlock = 1, star = 1, type = 2, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 255, attackprocess = "#002100000021", hp = 1500, powermax = 1000, physicalattack = 100, physicaldefence = 100, manaattack = 150, manadefence = 100, hit = 100, dodge = 0, crit = 20, crittimes = 200, anticrit = 0, antiattack = 35, attackbreak = 20, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 20, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 15, },
