    { heroid = 98, name = "少年殷洪", rank = 2, description = "商纣王的嫡次子，阐教仙人赤精子的徒弟。因遭妲己陷害，被纣王派人追杀，后被路过的赤精子所救并收为弟子，带往太华山云霄洞修炼，成为阐教三代门人之一。", autograph = "弟子愿保武王灭纣，望乞救命！", unlock = 1, star = 1, type = 1, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 110, attackprocess = "#0210111201", hp = 1000, powermax = 1200, physicalattack = 58, physicaldefence = 30, manaattack = 55, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 44, },
