    { heroid = 101, name = "少年邓九公", rank = 3, description = "成汤三山关总兵。闻太师奉纣王之命讨伐西歧命丧绝龙岭之后，纣王启用邓九公出兵攻打西周。后邓九公与女邓婵玉归周，斩杀商营多员大将，姜子牙封神，封邓九公为青龙星。 ", autograph = "大将专阃外之寄，正代天行拯溺之权。", unlock = 1, star = 1, type = 3, gender = 1, position = 4, weapon = 1, species = 1, scale = 0.48, attackrange = 170, attackprocess = "#2010001200100", hp = 1000, powermax = 1000, physicalattack = 60, physicaldefence = 30, manaattack = 60, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 50, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 28, },
