    { heroid = 103, name = "少年赵公明", rank = 3, description = "赵公明，又称赵玄坛，赵公元帅。“玄坛”是指道教的斋坛，也有护法之意，为道教四大元帅之一。同时为阴间雷部将帅和五方瘟神之一。又相传为正财神，司掌世间财源。 ", autograph = "生吾时天昏地暗，降吾时星斗未全。", unlock = 1, star = 1, type = 1, gender = 1, position = 3, weapon = 1, species = 1, scale = 0.48, attackrange = 150, attackprocess = "#12002100", hp = 1000, powermax = 1000, physicalattack = 60, physicaldefence = 30, manaattack = 60, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 13, },
    { heroid = 104, name = "少年木吒", rank = 3, description = "从小从师于阐教仙师普贤真人，在九宫山白鹤洞中修仙学道，习得百般艺，兵器为吴钩双剑，多次帮师叔姜子牙和周武王伐纣，最后辅助西周灭了商朝，从而肉身成圣。", autograph = "若将容易得，便作等闲看。", unlock = 1, star = 1, type = 1, gender = 1, position = 1, weapon = 1, species = 1, scale = 0.48, attackrange = 120, attackprocess = "#1020", hp = 1000, powermax = 1500, physicalattack = 60, physicaldefence = 30, manaattack = 60, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 8, },
    { heroid = 105, name = "少年邓婵玉", rank = 2, description = "商汤大将邓九公之女，玉虚弟子土行孙的妻子，年轻貌美，性格刚烈，有情有义，武艺不错，擅长刀法，原为商朝的女将，后来归顺大周，多次立下战功。", autograph = "甲胄无双貌出奇，娇羞袅娜更多姿。", unlock = 1, star = 1, type = 1, gender = 1, position = 2, weapon = 1, species = 1, scale = 0.48, attackrange = 180, attackprocess = "#01201222", hp = 1000, powermax = 1500, physicalattack = 60, physicaldefence = 30, manaattack = 60, manadefence = 30, hit = 100, dodge = 0, crit = 0, crittimes = 150, anticrit = 0, antiattack = 0, attackbreak = 0, antiphysicalattack = 0, physicalattackbreak = 0, antimanaattack = 0, manaattackbreak = 0, suckblood = 0, heal = 0, behealed = 0, antiangercost = 0, hprecover = 0, angerrecover = 0, angerrecover = 0, mark = 1, herospineid = 1, },
