GeneralShow = 
{
    { heroid = 1, name = "邓婵玉", type = 1, rank = 3, typelocation = "力量型输出系英雄", herodescription = "商汤大将邓九公之女，玉虚弟子土行孙的妻子，年轻貌美，性格刚烈，有情有义，武艺不错，擅长刀法，原为商朝的女将，后来归顺大周，多次立下战功。", icon = 2, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 2, name = "金狮", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 3, name = "殷郊", type = 3, rank = 4, typelocation = "敏捷型群攻系英雄", herodescription = "殷商大太子。殷洪的哥哥，在母亲姜王后被杀后，遭到纣王派兵追捕，殷郊和殷洪逃亡，走投无路之际，被官兵抓回朝歌判死刑，后被阐教仙人广成子救往九仙山桃源洞修道。", icon = 12, skilldescription = "浴血圣枪，殷郊以鲜血意志冲入敌方阵营造成大量范围伤害。", },
    { heroid = 4, name = "孔宣", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "原为“瞠目细冠红孔雀”（世间第一只孔雀），殷商驻三山关的镇关总兵，后调往镇守金鸡岭，骑马持刀，独门神通是“五色神光”，五行内无物不收。", icon = 17, skilldescription = "扇摇羽灭，积聚羽扇能量发射大量羽毛造成敌方全体极高伤害。", },
    { heroid = 5, name = "姬发", type = 2, rank = 4, typelocation = "智力型群控系英雄", herodescription = "西周王朝的开国君主。后排智力型英雄，核对敌方造成范围伤害并控制敌方全体。", icon = 22, skilldescription = "真龙出世，对敌方全体造成多段魔攻伤害并定身。", },
    { heroid = 6, name = "黄天化", type = 1, rank = 5, typelocation = "力量型群攻系全能英雄", herodescription = "原为凡人出身，是武成王黄飞虎的长子，阐教仙人清虚道德真君的二徒弟，师兄为杨任，是西周武将，为姜子牙座下四大先锋之一，助周伐纣的玉虚三代弟子之一。", icon = 27, skilldescription = "山崩地裂锤，黄天化击飞敌方全体，造成高额伤害。", },
    { heroid = 7, name = "琼宵仙子", type = 2, rank = 2, typelocation = "智力型群疗系英雄", herodescription = "截教通天教主座下弟子，与姐姐云霄仙子，姐姐碧霄仙子一同在三仙岛上修炼，过着与世无争的生活，坐骑为鸿鹄鸟，持有法宝金蛟剪。", icon = 32, skilldescription = "忘忧曲，在短时间内为队友回复生命值。", },
    { heroid = 8, name = "木吒", type = 1, rank = 4, typelocation = "力量型群控系英雄", herodescription = "从小从师于阐教仙师普贤真人，在九宫山白鹤洞中修仙学道，习得百般艺，兵器为吴钩双剑，多次帮师叔姜子牙和周武王伐纣，最后辅助西周灭了商朝，从而肉身成圣。", icon = 37, skilldescription = "万马奔腾，召唤战马冲向敌方，造成巨大伤害并眩晕全体敌人。", },
    { heroid = 9, name = "杨戬", type = 3, rank = 4, typelocation = "敏捷型爆发系英雄", herodescription = "二郎神杨戬，玉泉山金霞洞玉鼎真人的徒弟，阐教第三代，修成八九玄中妙，任尔纵横在世间，封清源妙道真君。神威显赫、善猎能战、正直仁义、为民除害、显圣护民。", icon = 42, skilldescription = "霸王戟，对敌方前排造成高额的物理伤害，并击退敌人。", },
    { heroid = 10, name = "圆真", type = 2, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 11, name = "神雕侠", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 12, name = "殷十娘", type = 3, rank = 5, typelocation = "敏捷型输出系英雄", herodescription = "哪吒之母，乐天率直、热情、有小聪明、善解人意、坚毅不屈、富幽默感、耳根软、好抱打不平、八卦、为达目的不惜讲美丽的谎言、一心相夫教子，家庭观念重。", icon = 57, skilldescription = "移形换影，闪现至敌人身边，造成大量伤害，且天生闪避极高。", },
    { heroid = 13, name = "赵公明", type = 1, rank = 3, typelocation = "力量型输出系英雄", herodescription = "赵公明，又称赵玄坛，赵公元帅。“玄坛”是指道教的斋坛，也有护法之意，为道教四大元帅之一。同时为阴间雷部将帅和五方瘟神之一。又相传为正财神，司掌世间财源。", icon = 62, skilldescription = "留下买路财，提高自身物攻伤害，并减少80%的所受伤害。", },
    { heroid = 14, name = "盈盈", type = 2, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 15, name = "姜子牙", type = 2, rank = 5, typelocation = "智力型单体系英雄", herodescription = "姜太公，周朝开国元勋，商末周初兵学奠基人。智力型后排输出英雄，可在造成高额伤害的同时治疗自身。", icon = 72, skilldescription = "愿者上钩，将鱼线抛向敌人，对敌方造成持续伤害并治疗自己。", },
    { heroid = 16, name = "鳄鱼", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 17, name = "土行孙", type = 2, rank = 4, typelocation = "智力型群攻系英雄", herodescription = "修行于夹龙山飞云洞，阐教第三代门人之一，身材矮小，不过四尺，面如土色，但本领高强，身手敏锐，以铁棍为兵器，以地行术称雄诸神；身负百年道行。", icon = 82, skilldescription = "轰炸毁灭，发射一枚威力巨大的火箭炮，对敌方全体造成伤害。", },
    { heroid = 18, name = "阿诺", type = 3, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 19, name = "鞋子猫", type = 3, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 20, name = "女娲娘娘", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "华夏民族人文先始，福佑社稷之正神。智力型后排英雄，可对敌方造成大量范围魔法伤害，并造成减速效果。", icon = 97, skilldescription = "毁天灭地，向敌方造成大量冰雹，造成大量伤害并减速。", },
    { heroid = 21, name = "金灵圣母", type = 2, rank = 2, typelocation = "智力型肉盾系英雄", herodescription = "道教女神斗姆元君。截教通天教主门下四大弟子之一，仅次于大师兄多宝道人，为女仙之首。法力高强，其道行更胜阐教十二金仙。座下弟子有闻仲、一气仙余元。", icon = 102, skilldescription = "震荡波，对前方所有敌人造成伤害并眩晕。", },
    { heroid = 22, name = "郑伦", type = 1, rank = 1, typelocation = "力量型肉盾系英雄", herodescription = "哼哈二将之一，手持水磨杵，为天界士兵，守卫着天界的安全。形象威武凶猛，一名郑伦，能鼻哼白气制敌。", icon = 107, skilldescription = "水磨旋风杖，挥舞禅杖，将敌人打飞至最后排并造成大量伤害。", },
    { heroid = 23, name = "斑点狗", type = 2, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 24, name = "南宫适", type = 1, rank = 4, typelocation = "力量型群控系英雄", herodescription = "南宫适，又称南宫子，西周著名贤者、重臣。南宫适是武王兴周灭纣时的贤臣。周武王灭商后，命南宫适拆掉纣王所建的鹿台，打开纣王的粮仓，以赈济贫弱奴隶。", icon = 117, skilldescription = "虎王将领，对敌方全体沉默，并且免疫控制持续十秒。", },
    { heroid = 25, name = "贾氏", type = 3, rank = 2, typelocation = "敏捷型群攻系英雄", herodescription = "黄飞虎之妻，因为古代女性无名，以娘家姓氏称呼，美貌端庄，贤淑温柔，深明大义，秉性刚烈，和黄飞虎的夫妻感情、黄妃的姑嫂感情都颇为深厚，是朝歌有名的贤妻良母。", icon = 122, skilldescription = "鹰击长空，对一条直线上所以英雄造成伤害并击退。", },
    { heroid = 26, name = "长颈鹿", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 27, name = "柳琵琶", type = 2, rank = 4, typelocation = "智力型群攻系英雄", herodescription = "鬼方国的郡主，轩辕三妖之一排行第三，其实她是玉石琵琶精，她嫁给李靖，她之后害死殷十娘和云中子，最后，她死在杨戬的天眼之下。", icon = 132, skilldescription = "花飞蝶舞，召唤蝶群不断对敌人造成持续伤害，并回复自身生命。", },
    { heroid = 28, name = "邓九公", type = 3, rank = 4, typelocation = "敏捷型输出系英雄", herodescription = "成汤三山关总兵。闻太师奉纣王之命讨伐西歧命丧绝龙岭之后，纣王启用邓九公出兵攻打西周。后邓九公与女邓婵玉归周，斩杀商营多员大将，姜子牙封神，封邓九公为青龙星。", icon = 137, skilldescription = "嗜血破天刃，对敌方单体造成多段伤害并使其暂时昏迷。", },
    { heroid = 29, name = "瑶姬", type = 3, rank = 3, typelocation = "敏捷型输出系英雄", herodescription = "炎帝之女，名曰瑶姬，未嫁而死，葬于巫山之阳，巫山神女斩杀十二妖龙、精魂依草，实为灵芝。为行船指点航路，为百姓驱除虎豹，为人间耕云播雨，为治病育种灵芝。", icon = 142, skilldescription = "鸾飞凤舞，对敌方造成范围伤害，并减少被攻击者的怒气。", },
    { heroid = 30, name = "苏妲己", type = 3, rank = 4, typelocation = "智力型辅助系英雄", herodescription = "苏氏，名妲，商纣王帝辛的妃子。后排智力型辅助英雄，可减少敌方阵营伤害。", icon = 147, skilldescription = "漫天花雨，对敌方全体造成伤害，并降低敌方物理伤害。", },
    { heroid = 31, name = "黄飞虎", type = 1, rank = 3, typelocation = "力量型肉盾系英雄", herodescription = "殷商纣王时期的武成王。原是凡人武将出身，精通武艺兵法，不会法术，使用兵器为金攥提芦枪，有神兽坐骑五色神牛。姜子牙封神，其被封为“东岳泰山天齐仁圣大帝”之神，", icon = 152, skilldescription = "惊天动地，猛击地面，对敌方后排造成物理伤害并眩晕。", },
    { heroid = 32, name = "后羿", type = 3, rank = 4, typelocation = "敏捷型爆发系英雄", herodescription = "后羿，本称羿、大羿、司羿，帝尧的射师，嫦娥的丈夫，射日英雄。羿在年仅五岁的时候被父母抛弃在深山，自幼在山林中成长。他善于射箭，助帝尧射下九日，匡扶黎明。", icon = 157, skilldescription = "流光神箭，可对敌方血量最少的英雄发起致命一击，秒杀对手。", },
    { heroid = 33, name = "帆帆", type = 3, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 34, name = "闻仲", type = 1, rank = 4, typelocation = "力量型肉盾系英雄", herodescription = "坐下墨麒麟，手使雌雄鞭，前排肉盾型力量英雄，可为队伍提供前排保护以及单体控制。", icon = 167, skilldescription = "铁链捆缚，将敌方单体绝对束缚并对其造成多段物理伤害。", },
    { heroid = 35, name = "碧霄仙子", type = 2, rank = 1, typelocation = "智力型群攻系英雄", herodescription = "截教通天教主座下亲传弟子，三霄中排行老二，与姐姐云霄仙子、妹妹琼霄仙子一同在三仙岛上修炼，三姐妹被世人称为三霄娘娘。坐骑为花翎鸟，持有法宝金蛟剪、混元金斗。", icon = 172, skilldescription = "天怒神罚，对敌方全体造成魔法伤害。", },
    { heroid = 36, name = "申公豹", type = 2, rank = 4, typelocation = "智力型群攻系英雄", herodescription = "与姜子牙同拜于元始天尊门下，为姜子牙的师弟。后排智力型英雄，可对敌方造成大量范围魔攻伤害。", icon = 177, skilldescription = "剑意滔天，召唤剑阵，刺杀敌方所有敌人，造成大量伤害。", },
    { heroid = 37, name = "胡喜媚", type = 3, rank = 4, typelocation = "敏捷型暗杀系英雄", herodescription = "千年九头雉鸡精，轩辕三妖之一排行第二，姐姐为苏妲己，妹妹为柳琵琶，奉女娲之命断送商朝。因轩辕坟被烧，才以妲己的义妹身份入宫，并化名胡喜媚，成为商纣王的宠妃。", icon = 182, skilldescription = "瞬步奇袭，给予单个目标致命一击造成高额伤害。", },
    { heroid = 38, name = "霸王龙", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 39, name = "龙吉公主", type = 3, rank = 5, typelocation = "敏捷型群控系英雄", herodescription = "乃昊天上帝之女，瑶池金母所生，助武王伐纣，精通水系法术，是周军得力战将之一，曾打败罗宣，砍伤孔宣，后被姜子牙封神，成为红鸾星。", icon = 192, skilldescription = "冰河时代，对敌方召唤暴风雪，造成巨大伤害并眩晕五秒。", },
    { heroid = 40, name = "云霄仙子", type = 2, rank = 4, typelocation = "智力型辅助系英雄", herodescription = "截教通天教主座下内门亲传弟子，三霄里最年长的仙女，为人善良，法力高强，云霄仙子在开天辟地前就已经成就道行，在三仙岛上化形。已经斩尽三尸，抛尽六气。", icon = 197, skilldescription = "心灵手巧，为己方全体编织一层护甲，可吸收魔法伤害。", },
    { heroid = 41, name = "金吒", type = 3, rank = 4, typelocation = "敏捷型爆发系英雄", herodescription = "为阐教仙人文殊广法天尊的弟子，修行于五龙山云霄洞，第三代玉虚门人之一，有师传法宝“遁龙桩”，有时手使宝剑战斗，曾奉师命下山助武王伐纣，多次帮姜子牙斩将杀敌。", icon = 202, skilldescription = "伪装秘术，进入伪装，攻速大幅度提高且完全免疫物攻伤害。", },
    { heroid = 42, name = "鸵鸟", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 43, name = "鹦鹉", type = 2, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 44, name = "殷洪", type = 1, rank = 3, typelocation = "力量型防御系英雄", herodescription = "商纣王的嫡次子，阐教仙人赤精子的徒弟。因遭妲己陷害，被纣王派人追杀，后被路过的赤精子所救并收为弟子，带往太华山云霄洞修炼，成为阐教三代门人之一。", icon = 217, skilldescription = "不动如山，在此无法移动，大幅度提高自身防御并反弹伤害。", },
    { heroid = 45, name = "啸天", type = 1, rank = 2, typelocation = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", herodescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", icon = 1, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 46, name = "姜王后", type = 2, rank = 4, typelocation = "智力型辅助系英雄", herodescription = "商纣王的原配王后，住在中宫，为人贤良，育有二子殷郊和殷洪。因群臣上奏纣王连日不早朝，而劝诫纣王，间接开罪妲己而遭其记恨。姜子牙封神时，其被封为“太阴星”之职。", icon = 227, skilldescription = "风魔乱舞，姜王后启动阵法，对敌方全提造成高额范围魔攻伤害。", },
    { heroid = 47, name = "神·琼宵仙子", type = 2, rank = 5, typelocation = "智力型群疗系英雄", herodescription = "截教通天教主座下弟子，与姐姐云霄仙子，姐姐碧霄仙子一同在三仙岛上修炼，过着与世无争的生活，坐骑为鸿鹄鸟，持有法宝金蛟剪。", icon = 32, skilldescription = "神之忘忧，忘忧曲升级版，为所有队友回复巨额生命值。", },
    { heroid = 48, name = "神·碧霄仙子", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "截教通天教主座下亲传弟子，三霄中排行老二，与姐姐云霄仙子、妹妹琼霄仙子一同在三仙岛上修炼，三姐妹被世人称为三霄娘娘。坐骑为花翎鸟，持有法宝金蛟剪、混元金斗。", icon = 172, skilldescription = "天怒神罚，对敌方全体造成魔法伤害。", },
    { heroid = 49, name = "神·郑伦", type = 1, rank = 5, typelocation = "力量型肉盾系英雄", herodescription = "哼哈二将之一，手持水磨杵，为天界士兵，守卫着天界的安全。形象威武凶猛，一名郑伦，能鼻哼白气制敌。", icon = 107, skilldescription = "水磨旋风杖，挥舞禅杖，将敌人打飞至最后排并造成大量伤害。", },
    { heroid = 50, name = "神·金灵圣母", type = 2, rank = 5, typelocation = "智力型肉盾系英雄", herodescription = "道教女神斗姆元君。截教通天教主门下四大弟子之一，仅次于大师兄多宝道人，为女仙之首。法力高强，其道行更胜阐教十二金仙。座下弟子有闻仲、一气仙余元。", icon = 102, skilldescription = "震荡波，对前方所有敌人造成伤害并眩晕。", },
    { heroid = 51, name = "神·贾氏", type = 3, rank = 5, typelocation = "敏捷型群攻系英雄", herodescription = "黄飞虎之妻，因为古代女性无名，以娘家姓氏称呼，美貌端庄，贤淑温柔，深明大义，秉性刚烈，和黄飞虎的夫妻感情、黄妃的姑嫂感情都颇为深厚，是朝歌有名的贤妻良母。", icon = 122, skilldescription = "鹰击长空，对一条直线上所以英雄造成伤害并击退。", },
    { heroid = 52, name = "神·申公豹", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "神·申公豹，姜子牙主持封神后便离开人间，回到神界回到其师坐下继续修炼。", icon = 177, skilldescription = "剑意滔天，召唤剑阵，刺杀敌方所有敌人，造成大量伤害。", },
    { heroid = 53, name = "神·苏妲己", type = 3, rank = 5, typelocation = "智力型辅助系英雄", herodescription = "神·苏妲己，封神过后苏妲己自愿投入炼狱之后悔思过错感动神界便将其引入神界塑造神体。", icon = 147, skilldescription = "漫天花雨，对敌方全体造成伤害，并降低敌方物理伤害。", },
    { heroid = 54, name = "神·土行孙", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "修行于夹龙山飞云洞，阐教第三代门人之一，身材矮小，不过四尺，面如土色，但本领高强，身手敏锐，以铁棍为兵器，以地行术称雄诸神；身负百年道行。", icon = 22, skilldescription = "真龙出世，对敌方全体造成多段魔攻伤害并定身。", },
    { heroid = 55, name = "神·姜王后", type = 2, rank = 5, typelocation = "智力型辅助系英雄", herodescription = "商纣王的原配王后，住在中宫，为人贤良，育有二子殷郊和殷洪。因群臣上奏纣王连日不早朝，而劝诫纣王，间接开罪妲己而遭其记恨。姜子牙封神时，其被封为“太阴星”之职。", icon = 227, skilldescription = "风魔乱舞，姜王后启动阵法，对敌方全提造成高额范围魔攻伤害。", },
    { heroid = 56, name = "神·龙吉公主", type = 3, rank = 5, typelocation = "敏捷型群控系英雄", herodescription = "乃昊天上帝之女，瑶池金母所生，助武王伐纣，精通水系法术，是周军得力战将之一，曾打败罗宣，砍伤孔宣，后被姜子牙封神，成为红鸾星。", icon = 192, skilldescription = "冰河时代，对敌方召唤暴风雪，造成巨大伤害并眩晕五秒。", },
    { heroid = 57, name = "神·后羿", type = 3, rank = 5, typelocation = "敏捷型爆发系英雄", herodescription = "后羿，本称羿、大羿、司羿，帝尧的射师，嫦娥的丈夫，射日英雄。羿在年仅五岁的时候被父母抛弃在深山，自幼在山林中成长。他善于射箭，助帝尧射下九日，匡扶黎明。", icon = 157, skilldescription = "流光神箭，可对敌方血量最少的英雄发起致命一击，秒杀对手。", },
    { heroid = 58, name = "神·女娲娘娘", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "神·女娲娘娘，女娲娘娘将自己的一魂一魄下界人间，其余留在神界镇守。", icon = 97, skilldescription = "毁天灭地，向敌方造成大量冰雹，造成大量伤害并减速。", },
    { heroid = 59, name = "神·孔宣", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "原为“瞠目细冠红孔雀”（世间第一只孔雀），殷商驻三山关的镇关总兵，后调往镇守金鸡岭，骑马持刀，独门神通是“五色神光”，五行内无物不收。", icon = 17, skilldescription = "扇摇羽灭，积聚羽扇能量发射大量羽毛造成敌方全体极高伤害。", },
    { heroid = 60, name = "神·云霄仙子", type = 2, rank = 5, typelocation = "智力型辅助系英雄", herodescription = "截教通天教主座下内门亲传弟子，三霄里最年长的仙女，为人善良，法力高强，云霄仙子在开天辟地前就已经成就道行，在三仙岛上化形。已经斩尽三尸，抛尽六气。", icon = 197, skilldescription = "心灵手巧，为己方全体编织一层护甲，可吸收魔法伤害。", },
    { heroid = 61, name = "神·胡喜媚", type = 3, rank = 5, typelocation = "敏捷型暗杀系英雄", herodescription = "千年九头雉鸡精，轩辕三妖之一排行第二，姐姐为苏妲己，妹妹为柳琵琶，奉女娲之命断送商朝。因轩辕坟被烧，才以妲己的义妹身份入宫，并化名胡喜媚，成为商纣王的宠妃。", icon = 182, skilldescription = "瞬步奇袭，给予单个目标致命一击造成高额伤害。", },
    { heroid = 62, name = "神·瑶姬", type = 3, rank = 5, typelocation = "敏捷型输出系英雄", herodescription = "炎帝之女，名曰瑶姬，未嫁而死，葬于巫山之阳，巫山神女斩杀十二妖龙、精魂依草，实为灵芝。为行船指点航路，为百姓驱除虎豹，为人间耕云播雨，为治病育种灵芝。", icon = 142, skilldescription = "鸾飞凤舞，对敌方造成范围伤害，并减少被攻击者的怒气。", },
    { heroid = 63, name = "神·柳琵琶", type = 2, rank = 5, typelocation = "智力型群攻系英雄", herodescription = "鬼方国的郡主，轩辕三妖之一排行第三，其实她是玉石琵琶精，她嫁给李靖，她之后害死殷十娘和云中子，最后，她死在杨戬的天眼之下。", icon = 132, skilldescription = "花飞蝶舞，召唤蝶群不断对敌人造成持续伤害，并回复自身生命。", },
    { heroid = 64, name = "神·姜子牙", type = 2, rank = 5, typelocation = "智力型单体系英雄", herodescription = "神·姜子牙，主持封神后姜子牙本意留在人界，但不放心神界众人，变化出一道法身进入神界陪同众人。", icon = 72, skilldescription = "愿者上钩，将鱼线抛向敌人，对敌方造成持续伤害并治疗自己。", },
    { heroid = 65, name = "神·殷郊", type = 3, rank = 5, typelocation = "敏捷型群攻系英雄", herodescription = "殷商大太子。殷洪的哥哥，在母亲姜王后被杀后，遭到纣王派兵追捕，殷郊和殷洪逃亡，走投无路之际，被官兵抓回朝歌判死刑，后被阐教仙人广成子救往九仙山桃源洞修道。", icon = 12, skilldescription = "浴血圣枪，殷郊以鲜血意志冲入敌方阵营造成大量范围伤害。", },
    { heroid = 66, name = "神·殷洪", type = 1, rank = 5, typelocation = "力量型防御系英雄", herodescription = "商纣王的嫡次子，阐教仙人赤精子的徒弟。因遭妲己陷害，被纣王派人追杀，后被路过的赤精子所救并收为弟子，带往太华山云霄洞修炼，成为阐教三代门人之一。", icon = 217, skilldescription = "不动如山，在此无法移动，大幅度提高自身防御并反弹伤害。", },
    { heroid = 67, name = "神·金吒", type = 3, rank = 5, typelocation = "敏捷型爆发系英雄", herodescription = "为阐教仙人文殊广法天尊的弟子，修行于五龙山云霄洞，第三代玉虚门人之一，有师传法宝“遁龙桩”，有时手使宝剑战斗，曾奉师命下山助武王伐纣，多次帮姜子牙斩将杀敌。", icon = 202, skilldescription = "伪装秘术，进入伪装，攻速大幅度提高且完全免疫物攻伤害。", },
    { heroid = 68, name = "神·黄飞虎", type = 1, rank = 5, typelocation = "力量型肉盾系英雄", herodescription = "殷商纣王时期的武成王。原是凡人武将出身，精通武艺兵法，不会法术，使用兵器为金攥提芦枪，有神兽坐骑五色神牛。姜子牙封神，其被封为“东岳泰山天齐仁圣大帝”之神，", icon = 152, skilldescription = "惊天动地，猛击地面，对敌方后排造成物理伤害并眩晕。", },
    { heroid = 69, name = "神·邓九公", type = 3, rank = 5, typelocation = "敏捷型输出系英雄", herodescription = "成汤三山关总兵。闻太师奉纣王之命讨伐西歧命丧绝龙岭之后，纣王启用邓九公出兵攻打西周。后邓九公与女邓婵玉归周，斩杀商营多员大将，姜子牙封神，封邓九公为青龙星。", icon = 137, skilldescription = "嗜血破天刃，对敌方单体造成多段伤害并使其暂时昏迷。", },
    { heroid = 70, name = "神·南宫适", type = 1, rank = 5, typelocation = "力量型群控系英雄", herodescription = "南宫适，又称南宫子，西周著名贤者、重臣。南宫适是武王兴周灭纣时的贤臣。周武王灭商后，命南宫适拆掉纣王所建的鹿台，打开纣王的粮仓，以赈济贫弱奴隶。", icon = 117, skilldescription = "虎王将领，对敌方全体沉默，并且免疫控制持续十秒。", },
    { heroid = 71, name = "神·赵公明", type = 1, rank = 5, typelocation = "力量型输出系英雄", herodescription = "赵公明，又称赵玄坛，赵公元帅。“玄坛”是指道教的斋坛，也有护法之意，为道教四大元帅之一。同时为阴间雷部将帅和五方瘟神之一。又相传为正财神，司掌世间财源。", icon = 62, skilldescription = "留下买路财，提高自身物攻伤害，并减少80%的所受伤害。", },
    { heroid = 72, name = "神·木吒", type = 1, rank = 5, typelocation = "力量型群控系英雄", herodescription = "从小从师于阐教仙师普贤真人，在九宫山白鹤洞中修仙学道，习得百般艺，兵器为吴钩双剑，多次帮师叔姜子牙和周武王伐纣，最后辅助西周灭了商朝，从而肉身成圣。", icon = 37, skilldescription = "万马奔腾，召唤战马冲向敌方，造成巨大伤害并眩晕全体敌人。", },
    { heroid = 73, name = "神·邓婵玉", type = 1, rank = 5, typelocation = "力量型输出系英雄", herodescription = "商汤大将邓九公之女，玉虚弟子土行孙的妻子，年轻貌美，性格刚烈，有情有义，武艺不错，擅长刀法，原为商朝的女将，后来归顺大周，多次立下战功。", icon = 2, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 74, name = "神·闻仲", type = 1, rank = 5, typelocation = "力量型肉盾系英雄", herodescription = "神·闻仲，闻仲在神界之中的本体，为神界的镇守大神。", icon = 167, skilldescription = "铁链捆缚，将敌方单体绝对束缚并对其造成多段物理伤害。", },
    { heroid = 75, name = "神·殷十娘", type = 3, rank = 5, typelocation = "敏捷型输出系英雄", herodescription = "哪吒之母，乐天率直、热情、有小聪明、善解人意、坚毅不屈、富幽默感、耳根软、好抱打不平、八卦、为达目的不惜讲美丽的谎言、一心相夫教子，家庭观念重。", icon = 57, skilldescription = "移形换影，闪现至敌人身边，造成大量伤害，且天生闪避极高。", },
    { heroid = 76, name = "神·杨戬", type = 3, rank = 5, typelocation = "敏捷型爆发系英雄", herodescription = "二郎神杨戬，玉泉山金霞洞玉鼎真人的徒弟，阐教第三代，修成八九玄中妙，任尔纵横在世间，封清源妙道真君。神威显赫、善猎能战、正直仁义、为民除害、显圣护民。", icon = 42, skilldescription = "霸王戟，对敌方前排造成高额的物理伤害，并击退敌人。", },
    { heroid = 77, name = "神·黄天化", type = 1, rank = 5, typelocation = "力量型群攻系全能英雄", herodescription = "原为凡人出身，是武成王黄飞虎的长子，阐教仙人清虚道德真君的二徒弟，师兄为杨任，是西周武将，为姜子牙座下四大先锋之一，助周伐纣的玉虚三代弟子之一。", icon = 27, skilldescription = "山崩地裂锤，黄天化击飞敌方全体，造成高额伤害。", },
    { heroid = 78, name = "神·姬发", type = 2, rank = 5, typelocation = "智力型群控系英雄", herodescription = "神·姬发，封神之后便进入神界，潜心修炼如今实力不同往日深不可测。", icon = 22, skilldescription = "真龙出世，对敌方全体造成多段魔攻伤害并定身。", },
    { heroid = 79, name = "少年琼宵", type = 2, rank = 1, typelocation = "智力型群疗系英雄", herodescription = "截教通天教主座下弟子，与姐姐云霄仙子，姐姐碧霄仙子一同在三仙岛上修炼，过着与世无争的生活，坐骑为鸿鹄鸟，持有法宝金蛟剪。", icon = 32, skilldescription = "神之忘忧，忘忧曲升级版，为所有队友回复巨额生命值。", },
    { heroid = 80, name = "少年碧霄", type = 2, rank = 1, typelocation = "智力型群攻系英雄", herodescription = "截教通天教主座下亲传弟子，三霄中排行老二，与姐姐云霄仙子、妹妹琼霄仙子一同在三仙岛上修炼，三姐妹被世人称为三霄娘娘。坐骑为花翎鸟，持有法宝金蛟剪、混元金斗。", icon = 172, skilldescription = "天怒神罚，对敌方全体造成魔法伤害。", },
    { heroid = 81, name = "少年郑伦", type = 1, rank = 1, typelocation = "力量型肉盾系英雄", herodescription = "哼哈二将之一，手持水磨杵，为天界士兵，守卫着天界的安全。形象威武凶猛，一名郑伦，能鼻哼白气制敌。", icon = 107, skilldescription = "水磨旋风杖，挥舞禅杖，将敌人打飞至最后排并造成大量伤害。", },
    { heroid = 82, name = "少年金灵", type = 2, rank = 1, typelocation = "智力型肉盾系英雄", herodescription = "道教女神斗姆元君。截教通天教主门下四大弟子之一，仅次于大师兄多宝道人，为女仙之首。法力高强，其道行更胜阐教十二金仙。座下弟子有闻仲、一气仙余元。", icon = 102, skilldescription = "震荡波，对前方所有敌人造成伤害并眩晕。", },
    { heroid = 83, name = "少年贾氏", type = 3, rank = 1, typelocation = "敏捷型群攻系英雄", herodescription = "黄飞虎之妻，因为古代女性无名，以娘家姓氏称呼，美貌端庄，贤淑温柔，深明大义，秉性刚烈，和黄飞虎的夫妻感情、黄妃的姑嫂感情都颇为深厚，是朝歌有名的贤妻良母。", icon = 122, skilldescription = "鹰击长空，对一条直线上所以英雄造成伤害并击退。", },
    { heroid = 84, name = "少年公豹", type = 2, rank = 2, typelocation = "智力型群攻系英雄", herodescription = "神·申公豹，姜子牙主持封神后便离开人间，回到神界回到其师坐下继续修炼。", icon = 177, skilldescription = "剑意滔天，召唤剑阵，刺杀敌方所有敌人，造成大量伤害。", },
    { heroid = 85, name = "少年妲己", type = 3, rank = 1, typelocation = "智力型辅助系英雄", herodescription = "神·苏妲己，封神过后苏妲己自愿投入炼狱之后悔思过错感动神界便将其引入神界塑造神体。", icon = 147, skilldescription = "漫天花雨，对敌方全体造成伤害，并降低敌方物理伤害。", },
    { heroid = 86, name = "少年土行孙", type = 2, rank = 2, typelocation = "智力型群攻系英雄", herodescription = "修行于夹龙山飞云洞，阐教第三代门人之一，身材矮小，不过四尺，面如土色，但本领高强，身手敏锐，以铁棍为兵器，以地行术称雄诸神；身负百年道行。", icon = 22, skilldescription = "真龙出世，对敌方全体造成多段魔攻伤害并定身。", },
    { heroid = 87, name = "少年姜王后", type = 2, rank = 3, typelocation = "智力型辅助系英雄", herodescription = "商纣王的原配王后，住在中宫，为人贤良，育有二子殷郊和殷洪。因群臣上奏纣王连日不早朝，而劝诫纣王，间接开罪妲己而遭其记恨。姜子牙封神时，其被封为“太阴星”之职。", icon = 227, skilldescription = "风魔乱舞，姜王后启动阵法，对敌方全提造成高额范围魔攻伤害。", },
    { heroid = 88, name = "少年龙吉", type = 3, rank = 3, typelocation = "敏捷型群控系英雄", herodescription = "乃昊天上帝之女，瑶池金母所生，助武王伐纣，精通水系法术，是周军得力战将之一，曾打败罗宣，砍伤孔宣，后被姜子牙封神，成为红鸾星。", icon = 192, skilldescription = "冰河时代，对敌方召唤暴风雪，造成巨大伤害并眩晕五秒。", },
    { heroid = 89, name = "少年后羿", type = 3, rank = 3, typelocation = "敏捷型爆发系英雄", herodescription = "后羿，本称羿、大羿、司羿，帝尧的射师，嫦娥的丈夫，射日英雄。羿在年仅五岁的时候被父母抛弃在深山，自幼在山林中成长。他善于射箭，助帝尧射下九日，匡扶黎明。", icon = 157, skilldescription = "流光神箭，可对敌方血量最少的英雄发起致命一击，秒杀对手。", },
    { heroid = 90, name = "少年女娲", type = 2, rank = 3, typelocation = "智力型群攻系英雄", herodescription = "神·女娲娘娘，女娲娘娘将自己的一魂一魄下界人间，其余留在神界镇守。", icon = 97, skilldescription = "毁天灭地，向敌方造成大量冰雹，造成大量伤害并减速。", },
    { heroid = 91, name = "少年孔宣", type = 2, rank = 3, typelocation = "智力型群攻系英雄", herodescription = "原为“瞠目细冠红孔雀”（世间第一只孔雀），殷商驻三山关的镇关总兵，后调往镇守金鸡岭，骑马持刀，独门神通是“五色神光”，五行内无物不收。", icon = 17, skilldescription = "扇摇羽灭，积聚羽扇能量发射大量羽毛造成敌方全体极高伤害。", },
    { heroid = 92, name = "少年云霄", type = 2, rank = 1, typelocation = "智力型辅助系英雄", herodescription = "截教通天教主座下内门亲传弟子，三霄里最年长的仙女，为人善良，法力高强，云霄仙子在开天辟地前就已经成就道行，在三仙岛上化形。已经斩尽三尸，抛尽六气。", icon = 197, skilldescription = "心灵手巧，为己方全体编织一层护甲，可吸收魔法伤害。", },
    { heroid = 93, name = "少年胡喜媚", type = 3, rank = 2, typelocation = "敏捷型暗杀系英雄", herodescription = "千年九头雉鸡精，轩辕三妖之一排行第二，姐姐为苏妲己，妹妹为柳琵琶，奉女娲之命断送商朝。因轩辕坟被烧，才以妲己的义妹身份入宫，并化名胡喜媚，成为商纣王的宠妃。", icon = 182, skilldescription = "瞬步奇袭，给予单个目标致命一击造成高额伤害。", },
    { heroid = 94, name = "少年瑶姬", type = 3, rank = 1, typelocation = "敏捷型输出系英雄", herodescription = "炎帝之女，名曰瑶姬，未嫁而死，葬于巫山之阳，巫山神女斩杀十二妖龙、精魂依草，实为灵芝。为行船指点航路，为百姓驱除虎豹，为人间耕云播雨，为治病育种灵芝。", icon = 142, skilldescription = "鸾飞凤舞，对敌方造成范围伤害，并减少被攻击者的怒气。", },
    { heroid = 95, name = "少年柳琵琶", type = 2, rank = 3, typelocation = "智力型群攻系英雄", herodescription = "鬼方国的郡主，轩辕三妖之一排行第三，其实她是玉石琵琶精，她嫁给李靖，她之后害死殷十娘和云中子，最后，她死在杨戬的天眼之下。", icon = 132, skilldescription = "花飞蝶舞，召唤蝶群不断对敌人造成持续伤害，并回复自身生命。", },
    { heroid = 96, name = "少年子牙", type = 2, rank = 3, typelocation = "智力型单体系英雄", herodescription = "神·姜子牙，主持封神后姜子牙本意留在人界，但不放心神界众人，变化出一道法身进入神界陪同众人。", icon = 72, skilldescription = "愿者上钩，将鱼线抛向敌人，对敌方造成持续伤害并治疗自己。", },
    { heroid = 97, name = "少年殷郊", type = 3, rank = 2, typelocation = "敏捷型群攻系英雄", herodescription = "殷商大太子。殷洪的哥哥，在母亲姜王后被杀后，遭到纣王派兵追捕，殷郊和殷洪逃亡，走投无路之际，被官兵抓回朝歌判死刑，后被阐教仙人广成子救往九仙山桃源洞修道。", icon = 12, skilldescription = "浴血圣枪，殷郊以鲜血意志冲入敌方阵营造成大量范围伤害。", },
    { heroid = 98, name = "少年殷洪", type = 1, rank = 1, typelocation = "力量型防御系英雄", herodescription = "商纣王的嫡次子，阐教仙人赤精子的徒弟。因遭妲己陷害，被纣王派人追杀，后被路过的赤精子所救并收为弟子，带往太华山云霄洞修炼，成为阐教三代门人之一。", icon = 217, skilldescription = "不动如山，在此无法移动，大幅度提高自身防御并反弹伤害。", },
    { heroid = 99, name = "少年金吒", type = 3, rank = 2, typelocation = "敏捷型爆发系英雄", herodescription = "为阐教仙人文殊广法天尊的弟子，修行于五龙山云霄洞，第三代玉虚门人之一，有师传法宝“遁龙桩”，有时手使宝剑战斗，曾奉师命下山助武王伐纣，多次帮姜子牙斩将杀敌。", icon = 202, skilldescription = "伪装秘术，进入伪装，攻速大幅度提高且完全免疫物攻伤害。", },
    { heroid = 100, name = "少年黄飞虎", type = 1, rank = 1, typelocation = "力量型肉盾系英雄", herodescription = "殷商纣王时期的武成王。原是凡人武将出身，精通武艺兵法，不会法术，使用兵器为金攥提芦枪，有神兽坐骑五色神牛。姜子牙封神，其被封为“东岳泰山天齐仁圣大帝”之神，", icon = 152, skilldescription = "惊天动地，猛击地面，对敌方后排造成物理伤害并眩晕。", },
    { heroid = 101, name = "少年邓九公", type = 3, rank = 2, typelocation = "敏捷型输出系英雄", herodescription = "成汤三山关总兵。闻太师奉纣王之命讨伐西歧命丧绝龙岭之后，纣王启用邓九公出兵攻打西周。后邓九公与女邓婵玉归周，斩杀商营多员大将，姜子牙封神，封邓九公为青龙星。", icon = 137, skilldescription = "嗜血破天刃，对敌方单体造成多段伤害并使其暂时昏迷。", },
    { heroid = 102, name = "少年南宫适", type = 1, rank = 2, typelocation = "力量型群控系英雄", herodescription = "南宫适，又称南宫子，西周著名贤者、重臣。南宫适是武王兴周灭纣时的贤臣。周武王灭商后，命南宫适拆掉纣王所建的鹿台，打开纣王的粮仓，以赈济贫弱奴隶。", icon = 117, skilldescription = "虎王将领，对敌方全体沉默，并且免疫控制持续十秒。", },
    { heroid = 103, name = "少年赵公明", type = 1, rank = 2, typelocation = "力量型输出系英雄", herodescription = "赵公明，又称赵玄坛，赵公元帅。“玄坛”是指道教的斋坛，也有护法之意，为道教四大元帅之一。同时为阴间雷部将帅和五方瘟神之一。又相传为正财神，司掌世间财源。", icon = 62, skilldescription = "留下买路财，提高自身物攻伤害，并减少80%的所受伤害。", },
    { heroid = 104, name = "少年木吒", type = 1, rank = 2, typelocation = "力量型群控系英雄", herodescription = "从小从师于阐教仙师普贤真人，在九宫山白鹤洞中修仙学道，习得百般艺，兵器为吴钩双剑，多次帮师叔姜子牙和周武王伐纣，最后辅助西周灭了商朝，从而肉身成圣。", icon = 37, skilldescription = "万马奔腾，召唤战马冲向敌方，造成巨大伤害并眩晕全体敌人。", },
    { heroid = 105, name = "少年邓婵玉", type = 1, rank = 1, typelocation = "力量型输出系英雄", herodescription = "商汤大将邓九公之女，玉虚弟子土行孙的妻子，年轻貌美，性格刚烈，有情有义，武艺不错，擅长刀法，原为商朝的女将，后来归顺大周，多次立下战功。", icon = 2, skilldescription = "十字剑光，邓婵玉对敌方造成范围伤害并击退敌人。", },
    { heroid = 106, name = "少年闻仲", type = 1, rank = 3, typelocation = "力量型肉盾系英雄", herodescription = "神·闻仲，闻仲在神界之中的本体，为神界的镇守大神。", icon = 167, skilldescription = "铁链捆缚，将敌方单体绝对束缚并对其造成多段物理伤害。", },
    { heroid = 107, name = "少年殷十娘", type = 3, rank = 3, typelocation = "敏捷型输出系英雄", herodescription = "哪吒之母，乐天率直、热情、有小聪明、善解人意、坚毅不屈、富幽默感、耳根软、好抱打不平、八卦、为达目的不惜讲美丽的谎言、一心相夫教子，家庭观念重。", icon = 57, skilldescription = "移形换影，闪现至敌人身边，造成大量伤害，且天生闪避极高。", },
    { heroid = 108, name = "少年杨戬", type = 3, rank = 3, typelocation = "敏捷型爆发系英雄", herodescription = "二郎神杨戬，玉泉山金霞洞玉鼎真人的徒弟，阐教第三代，修成八九玄中妙，任尔纵横在世间，封清源妙道真君。神威显赫、善猎能战、正直仁义、为民除害、显圣护民。", icon = 42, skilldescription = "霸王戟，对敌方前排造成高额的物理伤害，并击退敌人。", },
    { heroid = 109, name = "少年黄天化", type = 1, rank = 3, typelocation = "力量型群攻系全能英雄", herodescription = "原为凡人出身，是武成王黄飞虎的长子，阐教仙人清虚道德真君的二徒弟，师兄为杨任，是西周武将，为姜子牙座下四大先锋之一，助周伐纣的玉虚三代弟子之一。", icon = 27, skilldescription = "山崩地裂锤，黄天化击飞敌方全体，造成高额伤害。", },
    { heroid = 110, name = "少年姬发", type = 2, rank = 2, typelocation = "智力型群控系英雄", herodescription = "神·姬发，封神之后便进入神界，潜心修炼如今实力不同往日深不可测。", icon = 22, skilldescription = "真龙出世，对敌方全体造成多段魔攻伤害并定身。", },
}

