HelpManual = 
{
    { id = 1, _type = 1, team = 1, name = "如何变强", biaoti = "变强的途径", miaoshu = "1.英雄升级【英雄等级决定了英雄的综合属性上限，等级越高英雄的综合属性上限越高】*2.英雄升星【英雄升星决定了英雄的成长值，星数越高英雄的成长越高，6星后可按百分比提高属性】*3.英雄进阶【英雄进阶会大幅提升英雄的属性，并且进阶到对应等级会解锁新的技能。】*4.品质装备【装备的品质分为白、绿、蓝、紫、橙、红，红色品质为最佳】*5.稀有神器【均衡神器：轩辕剑、物攻神器：盘古斧、魔攻神器：伏羲琴、防御神器：昆仑镜。可根据英雄属性选择不同神器】*6.英雄兵书【英雄兵书可根据百分比极大幅度提高英雄属性，但需要耗费大量的韬略以及兵书材料】*7.装备升星【装备升星可根据百分比极大幅度提高装备属性，不同品质的装备加成不同】*8.装备洗练【装备洗练决定了装备的属性上限，洗练满的装备会使装备的潜力发挥到最大】*9.修炼升级【修炼分为：英雄修炼和装备修炼，使用英雄碎片和装备碎片进行修炼提升，可提高大量全英雄属性。】*更多提升战力的方式，请点击头像下方【我要变强】；寻找资源，请点击【我要资源】", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 2, _type = 1, team = 2, name = "英雄", biaoti = "简单的说明", miaoshu = "1.英雄品质【红>橙>紫>蓝>绿，可通过十连抽获取高星优质英雄。】\n2.英雄星级【可通过召唤石兑换英雄碎片进行升星，6星需要献祭英雄获取升星材料。】\n3.英雄等级【通过经验丹等材料对英雄进行升级，保持英雄等级为最高。】\n4.英雄进阶【可通过试炼之塔、趋吉避凶等玩法获得进阶材料为英雄进阶，进阶可获得巨大提升并开启新技能。】\n5.英雄心法【可通过悬赏任务获得内功要诀，配合英雄碎片提高英雄心法，可按百分比大幅度提升英雄属性】\n6.英雄技能【英雄技能可在实战中发挥巨大能量，技能越高战斗效果越强。】", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 3, _type = 1, team = 3, name = "装备", biaoti = "关于装备", miaoshu = "1.装备品质【红>橙>紫>蓝>绿>白，极品装备可通过七星坛、历练副本、神兵阁等途径获得】\n2.装备洗练【先将装备洗练只满属性后，会最大化激发装备潜力。】\n3.装备星级【通过不同品质的宝石对装备升星，会按百分比提高装备属性】\n4.装备强化【通过银两和强化石对装备进行强化，可稳定提升装备属性。】", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 4, _type = 1, team = 4, name = "神器", biaoti = "神器", miaoshu = "每个英雄可以佩戴一件神器，不同属性的英雄佩戴不同的神器，产生的效果不同。", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 5, _type = 1, team = 5, name = "种族", biaoti = "领地荣誉", miaoshu = "通过种族任务和领地战能够获取荣誉，可在种族商店兑换稀有道具奖励和稀有英雄。\n阵营势力点每周会进行一次评分，势力点高的阵营获胜，并且该阵营所有玩家能够获得大量荣誉奖励。", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 6, _type = 1, team = 6, name = "竞技场", biaoti = "抢夺资源", miaoshu = "竞技场中挑战其他玩家可以抢夺他的资源，被抢夺玩家拥有的资源越多，抢夺方能够抢夺的资源也就越多。*抢夺时也能够获得威望，威望累计到一定程度后能够提高自己的段位，段位越高每次抢夺时的额外奖励越多，在竞技场兑换的稀有道具就越多。", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 7, _type = 1, team = 7, name = "主城建筑", biaoti = "关于建筑", miaoshu = "钱庄：根据时间固定产出银两和翡翠，产出资源的数量随着建筑等级的提升而提高。\n七星坛：用于招募武将和抽取稀有装备道具，招募和抽奖均需要消耗对应的道具。\n英雄榜：用于查看全区不同项目的玩家排名情况。\n铁匠铺：用于合成或升级部分特殊道具。\n万宝阁：偶尔会出现极为稀有的英雄或道具供玩家兑换。\n邮件：用于接收各类通知信息以及物品。\n领地战：包含：领地战、王者雕塑、生命之树等玩法，可通过领地战玩法获得大量阵营点，在种族商店兑换稀有物品。\n图鉴：图鉴内展示了所有武将的介绍，供各位玩家了解武将的特色，更好地选择适合自己的武将。\n演武场：包含了大部分的日常活动，完成日常活动可获得大量培养必备材料。", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 8, _type = 1, team = 8, name = "演武场", biaoti = "关于演武场", miaoshu = "主城中央最显眼的建筑就是演武场啦！各种支线玩法都在演武场中，你可以在这里押运粮草，挑战世界boss，挑战赏金猎人，完成悬赏任务，挑战神器副本等很多很多玩法，如果你不知道还有什么新鲜功能没尝试，去演武场中看看总是没有错的！", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 9, _type = 1, team = 9, name = "任务", biaoti = "关于任务", miaoshu = "任务不是枯燥的机械劳动！而是指引你现在应该做哪些事情，任务分主线——记录了你的冒险进度；日常——记录了你每天都应该做哪些有意义的事情；至尊——记录了你的长远目标，至尊任务每一个都很难完成，但每一个都意味着你的水平又到了一个新的阶段！", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 10, _type = 1, team = 10, name = "帮派", biaoti = "关于帮派", miaoshu = "帮派是聚集了志同道合玩家的团体，加入帮派对于新手来说意味着更高的体力上限和诸多帮派奖励，对老手来说则是共同体验高级玩法，结交志同道合朋友的平台。所以一定要尽早加入一个可靠的帮派！", speed = 0, funcid = 0, idid = 0, needlevel = 0, },
    { id = 11, _type = 2, team = 1, name = "英雄", biaoti = "七星坛-群英降临", miaoshu = "使用英雄密令快速招募英雄和英雄碎片，每天可免费抽取1次，10连抽必得紫色英雄。还可以使用召唤石兑换英雄碎片。", speed = 5, funcid = "七星坛", idid = 18, needlevel = 8, },
    { id = 12, _type = 2, team = 1, name = "英雄", biaoti = "通过商店兑换", miaoshu = "参加各种活动都会获得各种独特的奖牌（货币），这些奖牌可以在相应商店中兑换独特的英雄。", speed = 3, funcid = "阵营兑换", idid = 9, needlevel = 32, },
    { id = 13, _type = 2, team = 1, name = "英雄", biaoti = "签到免费得英雄", miaoshu = "每月签到免费赠送1名英雄，还在等什么，快去看看吧。", speed = 3, funcid = "签到", idid = 20, needlevel = 1, },
    { id = 14, _type = 2, team = 2, name = "装备", biaoti = "七星坛-天降神兵", miaoshu = "七星坛可快速获取装备，每天免费抽取1次，每次抽奖必得一枚天星石，可用天星石免费兑换装备。", speed = 5, funcid = "七星坛装备", idid = 19, needlevel = 8, },
