    { id = 16, _type = 2, team = 2, name = "装备", biaoti = "天星石兑换装备", miaoshu = "七星坛-神兵异宝除了抽取装备以外，还可以用天星石来兑换装备。", speed = 4, funcid = "七星坛装备兑换", idid = 40, needlevel = 8, },
